INSERT INTO posts VALUES('edd68797-d5c6-4625-a490-c84da9511099','Produção Artística','Colaboramos com marcas e projetos especiais conectando o nome certo para falar com o público desejado. Além disso, atuamos junto a influenciadores e celebridades no desenvolvimento de conteúdos direcionados para cada talento, costurando parcerias e oportunidades. ','3ybw7xg762ci53x',1716338072223,1716338072223,'',NULL,'["{\"videos\":null,\"order\":0,\"fichaTecnica\":[],\"language\":\"ptbr\",\"socialMedia\":null,\"posttype\":\"tiposervicos\"}"]',NULL);
INSERT INTO posts VALUES('bfbc93a6-085d-49bf-9a0e-8c6bf15a258b','Serviços','Estamos preparados para atender todas as etapas de um projeto audiovisual, desde a sua criação até a entrega final, além de também oferecermos serviços fragmentados para atender a demandas específicas. Podemos, por exemplo, cuidar apenas da criação, apenas do roteiro, apenas da produção ou apenas da pós-produção.','3ybw7xg762ci53x',1716338064383,1716338064383,'',NULL,'["{\"videos\":null,\"order\":0,\"fichaTecnica\":[],\"language\":\"ptbr\",\"socialMedia\":null,\"posttype\":\"servicos\"}"]',NULL);
INSERT INTO posts VALUES('ef621f9b-1f8e-47e5-8310-888b0fbfd51e','Services','We are prepared to handle all stages of an audiovisual project, from its inception to final delivery, as well as offering fragmented services to meet specific demands. For example, we can take care of only the creation, only the screenplay, only the production, or only the post-production.','3ybw7xg762ci53x',1716338449099,1716338449099,'',NULL,'["{\"videos\":null,\"order\":0,\"fichaTecnica\":[],\"language\":\"enUS\",\"socialMedia\":null,\"posttype\":\"servicos\"}"]',NULL);
