INSERT INTO posts VALUES('1a3831d7-5cac-4a28-a1e9-ef9ac3f1ddee','CCXP Worlds 2021 – Road to Artists’ Valley by Santander','Série documental para CCXP 2021 sobre o coração do evento, o Artists Valley. Dividido em quatro episódios a série conta sobre a história das comicons até a chegada no grande evento da CCXP e a criação do Artists Valley o setor destinado aos quadrinistas no evento que virou a maior evento e a maior referência no universo dos quadrinhos no mundo. Criação, Roteiro e Produção Completa Farra para os parceiros do Omelete / CCXP.','3ybw7xg762ci53x',1716424269340,1717647999703,'https://bucket.farra.media/camada-38-1.png',NULL,'["{\"videos\":[\"https://vimeo.com/667404932\",\"https://vimeo.com/667413095\",\"https://vimeo.com/667418524\",\"https://vimeo.com/667428093\"],\"order\":0,\"fichaTecnica\":[{\"title\":\"Produção Executiva\",\"content\":\"Gui Vieira\"},{\"title\":\"Direção\",\"content\":\"Leo Parron\"},{\"title\":\"Gestão de Projetos\",\"content\":\"Yasmin Persi\"},{\"title\":\"Roteiro\",\"content\":\"André Brandt e Leo Parron\"}],\"language\":\"ptbr\",\"socialMedia\":null,\"posttype\":\"jobs\"}"]','ccxp-worlds-2021-road-to-artists-valley-by-santander');
