INSERT INTO posts VALUES('123f49f6-8786-48d1-8cdd-8f6416de2de2','Rennova | O Poder da Escolha','A Farra foi a produtora responsável pelo roteiro e pela produção da peça publicitária para a marca Rennova, uma linha de produtos para procedimentos estéticos injetáveis que sublinha a importância de escolher o bem-estar. E essa produção contou com a participação das embaixadoras Giovanna Antonelli e Deborah Secco! Foi um projeto ambicioso e desafiador. A Farra, em parceria e sinergia com a Rennova, criou e produziu esse filme, que foi gravado no Rio de Janeiro e adaptado tanto para exibição na TV quanto para veiculação na internet, além de conteúdos para a Landing Page da marca, proporcionando uma experiência envolvente para o público-alvo da Rennova.','3ybw7xg762ci53x',1716477546278,1717648006773,'',NULL,'["{\"videos\":\"https://vimeo.com/869568669\",\"order\":0,\"fichaTecnica\":[{\"title\":\"Criação\",\"content\":\"André Brandt, Sérgio Barbosa e Rodrigo Moura\"},{\"title\":\"Direção\",\"content\":\"Hugo Moura\"},{\"title\":\"Diretora de Produção\",\"content\":\"Juliana Hodas\"},{\"title\":\"Produção Executiva\",\"content\":\"Gui Vieira e Tayna Adam\"},{\"title\":\"Produção\",\"content\":\"Farra\"},{\"title\":\"Montador\",\"content\":\"Lucas Araujo\"},{\"title\":\"Coordenação de Pós Produção\",\"content\":\"Fred Kesselring\"},{\"title\":\"Direção de Produção\",\"content\":\"Ju Hodas\"},{\"title\":\"Assistente de Produção Executiva\",\"content\":\"Beatriz Fontana\"}],\"language\":\"ptbr\",\"socialMedia\":null,\"posttype\":\"jobs\"}"]','rennova-o-poder-da-escolha');
