INSERT INTO posts VALUES('2416c4ee-6309-42d3-9442-3bfa0b4e422f','Supositório','Apresentado por André Brandt, Supositório é um espaço para imaginar outras realidades que seriam possíveis caso algo de diferente tivesse acontecido na história. Junto com ele, sempre duas celebridades embarcam nessas suposições absurdas que com certeza já passaram pela sua cabeça, ou se não, vão passar agora. Supositório é um podcast Original Spotify produzido pela Farra. Episódios novos às segundas-feiras.','3ybw7xg762ci53x',1716478736827,1717648023438,'https://bucket.farra.media/supositorio.png',NULL,'["{\"videos\":\"https://vimeo.com/744341142\",\"order\":0,\"fichaTecnica\":[{\"title\":\"Direção\",\"content\":\"André Brandt\"},{\"title\":\"Roteiro\",\"content\":\"Ste Marques\"},{\"title\":\"Edição\",\"content\":\"Lucas Araújo\"},{\"title\":\"Gestão de Projeto\",\"content\":\"Rosa Taques\"},{\"title\":\"Arte\",\"content\":\"Beatriz Vidal\"},{\"title\":\"Produção\",\"content\":\"Farra\"}],\"language\":\"ptbr\",\"socialMedia\":null,\"posttype\":\"jobs\"}"]','supositorio');
